import React, { useEffect, useState } from "react";
import Card from "../Card";
import Filter from "../Filter";
import { priorityMap, statusMap } from "../../constants";

function getInitialLocalState(key) {
  const data = localStorage.getItem("kanbanBoard");
  const parsedData = JSON.parse(data);
  return parsedData && parsedData[key];
}

function getBackgroundColor() {
  return Math.random() * 255;
}

const getShortName = (user) =>
  user.name
    .split(" ")
    .map((word) => {
      return word[0].toUpperCase();
    })
    .join("");

function KanbanBoard({ data }) {
  const [displayData, setDisplayData] = useState({});
  const [ordering, setOrdering] = useState(() => {
    return getInitialLocalState("orderBy") || "priority";
  });
  const [grouping, setGrouping] = useState(() => {
    return getInitialLocalState("groupBy") || "status";
  });

  const { tickets, users } = data;
  useEffect(() => {
    const displayDataTemp = {};
    tickets?.forEach((item) => {
      if (displayDataTemp[item[grouping]]) {
        displayDataTemp[item[grouping]].push(item);
      } else {
        displayDataTemp[item[grouping]] = [item];
      }
    });
    Object.entries(displayDataTemp).forEach(([key, value]) => {
      value.sort((a, b) => a[ordering] - b[ordering]);
      displayDataTemp[key] = value;
    });
    setDisplayData(displayDataTemp);
  }, [grouping, ordering, tickets]);

  useEffect(() => {
    localStorage.setItem(
      "kanbanBoard",
      JSON.stringify({ orderBy: ordering, groupBy: grouping })
    );
  }, [displayData]);

  console.log("displayData", displayData);

  const usersMap = users?.reduce((obj, user) => {
    obj[user.id] = user;

    return obj;
  }, {});

  // console.log("statusEnum",statusEnum)

  return (
    users &&
    tickets && (
      <div style={{ position: "relative", padding: "20px 40px" }}>
        <Filter
          setGrouping={(value) => setGrouping(value)}
          setOrdering={setOrdering}
          grouping={grouping}
          ordering={ordering}
        />
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            overflowX: "scroll",
            height: "100vh",
            margin: "auto",
            backgroundColor: "#f3f5f9",
          }}
        >
          {Object.entries(displayData)?.map(([groupBy, groupList], index) => {
            return (
              <div
                key={index}
                style={{ display: "flex", flexDirection: "column" }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    margin: "20px",
                    padding: "10px",
                    fontWeight: "600",
                  }}
                >
                  <span>
                    {grouping === "priority" && (
                      <span>
                        <img src={priorityMap[groupBy]?.iconPath} alt="" />{" "}
                        {priorityMap[groupBy]?.name}
                      </span>
                    )}
                    {grouping === "status" && (
                      <span>
                        <img src={statusMap[groupBy]?.iconPath} alt="" />{" "}
                        {statusMap[groupBy]?.name}
                      </span>
                    )}
                    {grouping === "userId" && (
                      <span>
                        <div
                          style={{
                            alignItems: "center",
                            justifyContent: "center",
                            display: "inline-flex",
                            marginRight: "10px",
                            position: "relative",
                            fontSize: "0.8rem",
                            borderRadius: "50%",
                            width: "30px",
                            height: "30px",
                            backgroundColor: `rgb(${getBackgroundColor()}, ${getBackgroundColor()}, ${getBackgroundColor()})`,
                          }}
                        >
                          {usersMap[groupBy] && getShortName(usersMap[groupBy])}
                        </div>
                        {usersMap[groupBy]?.name}
                      </span>
                    )}

                    <span style={{ paddingLeft: "10px", color: "gray" }}>
                      {groupList.length}
                    </span>
                  </span>
                  <span>
                    <img
                      src="/svgIcons/add.svg"
                      alt="add Icon"
                      style={{ paddingRight: "10px" }}
                    />
                    <img
                      src="/svgIcons/three_dot_menu.svg"
                      alt="More options Menu"
                    />
                  </span>
                </div>
                <div>
                  {groupList.map((group, index) => {
                    return (
                      <Card
                        grouping={grouping}
                        key={`${groupBy}-${index}`}
                        ticket={group}
                        user={usersMap[group.userId]}
                      />
                    );
                  })}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    )
  );
}

export default KanbanBoard;
