import React, { useEffect, useState } from "react";
import Card from "../Card";
import Filter from "../Filter";

function KanbanBoard({ data }) {
  const [displayData, setDisplayData] = useState({});
  const [ordering, setOrdering] = useState("priority");
  const [grouping, setGrouping] = useState("status");
  let userEnum = {};

  const { tickets, users } = data;
  useEffect(() => {
    const displayDataTemp = {};
    tickets?.forEach((item) => {
      if (displayDataTemp[item[grouping]]) {
        displayDataTemp[item[grouping]].push(item);
      } else {
        displayDataTemp[item[grouping]] = [item];
      }
    });
    Object.entries(displayDataTemp).forEach(([key, value]) => {
      value.sort((a, b) => a[ordering] - b[ordering]);
      displayDataTemp[key] = value;
    });
    setDisplayData(displayDataTemp);
  }, [grouping, ordering]);

  useEffect(()=>{

  }, [data.length])

  console.log("displayData", displayData);

  const usersMap = users?.reduce((obj, user) => {
    obj[user.id] = user;
    return obj;
  }, {});

  return (
    <div style={{ position: "relative", padding: "20px 40px"}}>
      <Filter
        setGrouping={(value) => setGrouping(value)}
        setOrdering={setOrdering}
        grouping={grouping}
        ordering={ordering}
      />
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          overflowX: "scroll",
          height: "100vh",
          margin: "auto",
          backgroundColor: "#f3f5f9",
        }}
      >
        {Object.entries(displayData)?.map(([groupBy, groupList]) => {
          return (
            <div style={{ display: "flex", flexDirection: "column" }}>
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  margin: "20px",
                  padding: "10px",
                  fontWeight: "600",
                }}
              >
                <span>
                  {groupBy}
                  <span style={{paddingLeft:"10px", color: "gray" }}>{groupList.length}</span>
                </span>
                <span>
                  <img
                    src="/svgIcons/add.svg"
                    alt="add Icon"
                    style={{ paddingRight: "10px" }}
                  />
                  <img
                    src="/svgIcons/three_dot_menu.svg"
                    alt="More options Menu"
                  />
                </span>
              </div>
              <div>
                {groupList.map((group) => {
                  return <Card ticket={group} user={usersMap[group.userId]} />;
                })}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default KanbanBoard;
