import React from "react";

function Card({
    ticket,
    user
}) {
  const { id, title, tag, status, userId, priority } = ticket;

  return (
    <div
      style={{
        border: "1px solid black",
        borderRadius: "5px",
        padding: "5px",
        boxShadow: "5px 5px 30px rgba(0, 0, 0, 0.3)",
        width: "300px", 
        margin: "20px",
        textAlign: "left" 
      }}
    >
      <div
        style={{display: "flex", justifyContent: "space-between", padding: "5px 5px 5px 10px" }}
      >
        <div> {id}</div>
        <div style={{borderRadius: "50%", width: "20px", height: "20px", border: "1px solid black"}}>{user.name.split(" ").map((word)=> word[0].toUppercase())} </div>
      </div>
      <div
        style={{ justifyContent: "space-between", padding: "5px 5px 5px 10px" }}
      >
        <div> {status}</div>
        <div>{title}</div>
      </div>
      <div
        style={{ justifyContent: "space-between", padding: "5px 5px 5px 10px" }}
      >
       {tag.map((tagItem)=><span style={{paddingRight: "5px"}}>{tagItem}</span>)}
      </div>
    </div>
  );
}

export default Card;
