import React from "react";

function Card({
    ticket,
    user
}) {
    console.log("ticket", "user", ticket, user)
  const { id, title, tag, status, userId, priority } = ticket;
    const shortName = user.name.split(" ").map((word)=> {return word[0].toUppercase()}).join("");
    console.log("shortName",shortName)
  return (
    <div
      style={{
        border: "1px solid black",
        borderRadius: "5px",
        padding: "5px",
        boxShadow: "5px 5px 30px rgba(0, 0, 0, 0.3)",
        width: "300px", 
        margin: "20px",
        textAlign: "left" 
      }}
    >
      <div
        style={{display: "flex", justifyContent: "space-between", padding: "5px 5px 5px 10px" }}
      >
        <div> {userId}</div>
        <div style={{borderRadius: "50%", width: "20px", height: "20px", border: "1px solid black"}}><img src="/svgIcons/person.svg" width= "20px" height= "20px"  alt="image" /></div>
      </div>
      <div
        style={{ justifyContent: "space-between", padding: "5px 5px 5px 10px" }}
      >
        <div> {status}</div>
        <div>{title}</div>
      </div>
      <div
        style={{ justifyContent: "space-between", padding: "5px 5px 5px 10px" }}
      >
       {tag.map((tagItem)=><span style={{paddingRight: "5px"}}>{tagItem}</span>)}
      </div>
    </div>
  );
}

export default Card;
