import React from 'react'

function ProfileIcon({user}) {

    const shortName = user.name
    .split(" ")
    .map((word) => {
      return word[0].toUpperCase();
    })
    .join("");

  return (
    <>
    <div
      style={{
        alignItems: "center",
        justifyContent: "center",
        display: "flex",
        position: "relative",
        fontSize: "0.8rem",
        borderRadius: "50%",
        width: "30px",
        height: "30px",
        backgroundColor: getBackgroundColor(user.userId),
      }}
    >
      {shortName}
      <div
        style={{
          position: "absolute",
          bottom: "0px",
          right: "0px",
          width: "10px",
          height: "10px",
          border: "1px solid black",
          borderRadius: "50%",
          backgroundColor: user.available ? "green" : "lightgray",
        }}
      ></div>
    </div>
  </>
  )
}

export default ProfileIcon
