import React from "react";
import { priorityMap } from "../../constants";
// import { statusEnum } from "../../constants";

function getBackgroundColor(){
    return Math.random() * 255
}

function Card({ ticket, user, grouping }) {
  console.log("ticket", "user", ticket, user);
  const { id, title, tag, status, userId, priority } = ticket;
  const shortName = user.name.split(" ").map((word) => {
      return word[0].toUpperCase();
    })
    .join("");
  return (
    <div
      style={{
        borderRadius: "5px",
        padding: "10px",
        boxShadow: "1px 1px 3px rgba(0, 0, 0, 0.2)",
        width: "300px",
        height: "150px",
        minWidth:"300px",
        margin: "20px",
        textAlign: "left",
        position:"relative",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          padding: "5px 5px 5px 10px",
        }}
      >
        <div style={{ color: "gray", fontWeight: "600" }}> {id}</div>
        {grouping === "userId" && <><div
          style={{
            alignItems: "center",
            justifyContent: "center",
            display: "flex",
            position: "relative",
            fontSize: "0.8rem",
            borderRadius: "50%",
            width: "30px",
            height: "30px",
            backgroundColor: `rgb(${getBackgroundColor()}, ${getBackgroundColor()}, ${getBackgroundColor()})`
          }}
        >
          {/* <img
            src="/svgIcons/person.svg"
            width="20px"
            height="20px"
            alt="image"
          /> */}
        {shortName}
        <div style={{position:"absolute", bottom: "0px", right: "0px",  width: "10px", height:"10px", border:"1px solid black", borderRadius:"50%", backgroundColor: user.available? "green": "lightgray"}}></div>
        </div></>}
      </div>

      <div
        style={{ justifyContent: "space-between", padding: "5px 5px 5px 10px", }}
      >
        <div style={{ display: "flex", alignItems: "flex-start" }}>
          <span
            style={{
              paddingRight: "5px",
              boxSizing: "border-box",
              width: "20px",
            }}
          >
          </span>
          {title}
        </div>
      </div>
      <div
        style={{position:"absolute", padding: "5px 5px 5px 10px", bottom: "10px" }}
      >
        {grouping !== "priority" &&<img style={{verticalAlign:"middle"}} src = {priorityMap[priority].iconPath} /> }
        {tag.map((tagItem) => (

          <span
            style={{paddingLeft: "5px", color: "gray", fontWeight: "600" }}
          >
            {tagItem}
          </span>
        ))}
      </div>
    </div>
  );
}

export default Card;
