import React from 'react'

function Select({options, onOptionChange, selectedValue}) {
  return (
    <select onChange={onOptionChange}>
        {options.map(option=><option  value={option.value} selected={option.value === selectedValue }>{option.name}</option>)}

    </select>
  )
}

export default Select
