import React from 'react'

function Select({options, onOptionChange, selectedValue}) {
  return (
    <select onChange={(e)=>{ console.log("event",e.target.value);onOptionChange(e.target.value)}} defaultValue={selectedValue}>
        {options.map((option, index)=><option key ={index} value={option.value} >{option.name}</option>)}

    </select>
  )
}

export default Select
