import React, { useState } from "react";
import ToggleArrow from "../ToggleArrow";
import FilterModel from "../FilterModel";

function Filter({setOrdering, setGrouping, grouping, ordering}) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <div style={{ position: "relative" }}>
        <div
          style={{
            margin: "10px",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid gray",
            outline: "#e3e5e7",
            display: "inline-block",
            fontWeight: "600",
            boxShadow: "1px 1px 3px rgba(0, 0, 0, 0.2)",
          }}
          onClick={(e) => {
            setIsOpen(!isOpen);
            e.stopPropagation();
          }}
        >
          <img width="15px" src="/svgIcons/filters.svg" /> Display {" "}
          <ToggleArrow isOpen={isOpen} />
        </div>
        <FilterModel setOrdering={setOrdering} setGrouping={setGrouping} grouping={grouping} ordering={ordering} show={isOpen} />
      </div>
    </>
  );
}

export default Filter;
