import React, { useRef, useState, useEffect } from "react";
import ToggleArrow from "../ToggleArrow";
import FilterModel from "../FilterModel";

function Filter({setOrdering, setGrouping, grouping, ordering}) {
  const [isOpen, setIsOpen] = useState(false);
  const btnRef = useRef()

  useEffect(() => {
    const closeDropdown = e => {
        if (!btnRef.current.contains(e.target)) {
            setIsOpen(false);
        }
    }
    document.body.addEventListener('click', closeDropdown)
    return () => document.body.removeEventListener('click', closeDropdown)
}, [btnRef])

  return (
    <>
      <div style={{ position: "relative" }} >
        <div
          style={{
            marginBottom:"10px",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #e9ebed",
            outline: "#e3e5e7",
            display: "inline-block",
            fontWeight: "600",
            boxShadow: "1px 1px 3px rgba(0, 0, 0, 0.2)",
          }}
          onClick={(e) => {
            setIsOpen(!isOpen);
            e.stopPropagation();
          }}
        >
          <img width="15px" src="/svgIcons/filters.svg" /> Display {" "}
          <ToggleArrow isOpen={isOpen} />
        </div>
        {isOpen && <FilterModel ref={btnRef} setOrdering={setOrdering} setGrouping={setGrouping} grouping={grouping} ordering={ordering} />}
      </div>
    </>
  );
}

export default Filter;
