import React from "react";
import { priorityMap, statusMap } from "../../constants";
import ProfileIcon from "../ProfileIcon";

function Card({ ticket, user, grouping }) {
  console.log("ticket", "user", ticket, user);
  const { id, title, tag, status, userId, priority } = ticket;

  return (
    <div
    className="card-wrapper"
 >
      <div
      className="card-header"

      >
        <div style={{ color: "gray", fontWeight: "600" }}> {id}</div>
        {grouping !== "userId" && (
          <ProfileIcon user ={user}  />
        )}
      </div>

      <div
        style={{ justifyContent: "space-between", padding: "5px 5px 5px 10px" }}
      >
        <div style={{ display: "flex", alignItems: "flex-start" }}>
          <span
            style={{
              paddingRight: "5px",
              boxSizing: "border-box",
              width: "20px",
            }}
          >
            {grouping !== "status" && (
              <img
                style={{ verticalAlign: "middle" }}
                src={statusMap[status].iconPath}
              />
            )}
          </span>
          {title}
        </div>
      </div>
      <div
        style={{
          position: "absolute",
          padding: "5px 5px 5px 10px",
          bottom: "10px",
        }}
      >
        {grouping !== "priority" && (
          <img
            style={{
              verticalAlign: "middle",
              border: "1px solid lightgray",
              borderRadius: "3px",
              padding: "2px",
            }}
            src={priorityMap[priority].iconPath}
          />
        )}
        {tag.map((tagItem) => (
          <span
            style={{
              marginLeft: "5px",
              padding: "0px 5px",
              color: "gray",
              border: "1px solid lightgray",
              borderRadius: "3px",
              fontWeight: "600",
            }}
          >
            {tagItem}
          </span>
        ))}
      </div>
    </div>
  );
}

export default Card;
