import React, { useState } from 'react'
import Card from '../Card'
import Filter from '../Filter';

function KanbanBoard({data}) {
const {tickets, users} = data;  

const usersMap = users?.reduce((obj,user)=>{
     obj[user.id]= user;
     return obj;
},{})



  return <div style={{display:"flex", justifyContent:"center", flexDirection:"column", position:"relative"}}><Filter />
  <div style={{ display: "flex", flexDirection:"column", height: "100vh",  flexWrap:"wrap", margin: "auto", backgroundColor: "lightgrey"}}>{tickets?.map((ticket)=>{
        return <Card ticket = {ticket} user={usersMap[ticket.userId]}/>
    })}</div>
    </div>

}

export default KanbanBoard
