import React, { useEffect, useState } from "react";
import Card from "../Card";
import Filter from "../Filter";

function KanbanBoard({ data }) {

  const [displayData, setDisplayData] = useState({})  
  const [ordering, setOrdering] = useState(null);
  const [grouping, setGrouping] = useState(null);

  const { tickets, users } = data;
useEffect(()=>{
    const displayDataTemp = {};
    tickets?.forEach((item)=>{
       if( displayDataTemp[item[grouping]]){
        displayDataTemp[item[grouping]].push(item);
       }
       else {
        displayDataTemp[item[grouping]] = [item];
       }
    });
    Object.entries(displayDataTemp)?.forEach((key, value)=>{
        displayDataTemp[key] = value.sort((a,b) => a[ordering] - b[ordering])
    })
    setDisplayData(displayDataTemp)

},[grouping, ordering])

console.log("displayData", displayData);



  const usersMap = users?.reduce((obj, user) => {
    obj[user.id] = user;
    return obj;
  }, {});

  return (
    <div style={{ position: "relative" }}>
      <Filter setGrouping={(value)=>setGrouping(value)} setOrdering={setOrdering} />
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          height: "100vh",
          flexWrap: "wrap",
          margin: "auto",
          backgroundColor: "#f3f5f9",
        }}
      >
        {tickets?.map((ticket) => {
          return <Card ticket={ticket} user={usersMap[ticket.userId]} />;
        })}
      </div>
    </div>
  );
}

export default KanbanBoard;
