import React from "react";
import { statusEnum } from "../../constants";

function getBackgroundColor(){
    return Math.random() * 255
}

function Card({ ticket, user }) {
  console.log("ticket", "user", ticket, user);
  const { id, title, tag, status, userId, priority } = ticket;
  const shortName = user.name.split(" ").map((word) => {
      return word[0].toUpperCase();
    })
    .join("");
  return (
    <div
      style={{
        borderRadius: "5px",
        padding: "10px",
        boxShadow: "2px 2px 2px rgba(0, 0, 0, 0.3)",
        width: "300px",
        height: "150px",
        minWidth:"300px",
        margin: "20px",
        textAlign: "left",
        position:"relative",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          padding: "5px 5px 5px 10px",
        }}
      >
        <div style={{ color: "gray", fontWeight: "600" }}> {id}</div>
        <div
          style={{
            alignItems: "center",
            justifyContent: "center",
            display: "flex",
            position: "relative",
            fontSize: "0.8rem",
            borderRadius: "50%",
            width: "20px",
            height: "20px",
            backgroundColor: `rgb(${getBackgroundColor()}, ${getBackgroundColor()}, ${getBackgroundColor()})`
          }}
        >
          {/* <img
            src="/svgIcons/person.svg"
            width="20px"
            height="20px"
            alt="image"
          /> */}
        {shortName}
        </div>
      </div>
      <div
        style={{ justifyContent: "space-between", padding: "5px 5px 5px 10px", }}
      >
        <div style={{ display: "flex", alignItems: "flex-start" }}>
          <span
            style={{
              paddingRight: "5px",
              boxSizing: "border-box",
              width: "20px",
            }}
          >
            {status === 'todo' && <img src="/svgIcons/todo.svg" alt="todo Icon" />}
            {status === 'in-progress' &&  <img src="/svgIcons/inProgress.svg" alt="In Progress Icon" />}
            {status === 'done' &&  <img src="/svgIcons/check.svg" alt="Done Icon" />} 
          </span>
          {title}
        </div>
      </div>
      <div
        style={{position:"absolute", padding: "5px 5px 5px 10px", bottom: "10px" }}
      >
        {tag.map((tagItem) => (
          <span
            style={{ paddingRight: "5px", color: "gray", fontWeight: "600" }}
          >
            {tagItem}
          </span>
        ))}
      </div>
    </div>
  );
}

export default Card;
